import React, { useRef, useState } from 'react';
import './Contact.css';
import emailjs from '@emailjs/browser';

const Contact = () => {
  const form = useRef();
  const [showForm, setShowForm] = useState(true);
  const [showSuccessMessage, setShowSuccessMessage] = useState(false);

  const sendEmail = (e) => {
    e.preventDefault();

    emailjs
      .sendForm('service_zqe39n6', 'template_d1zg3jd', form.current, {
        publicKey: 'dcF3Q2XjKqQ3HJZNU',
      })
      .then(
        () => {
          setShowForm(false);
          setShowSuccessMessage(true);
          console.log('SUCCESS!');
        },
        (error) => {
          console.log('FAILED...', error.text);
        },
      );
  };

  return (
    <section className='contact container section' id='contact'>
      <h2 className='section__title'>Get In Touch</h2>

      <div className='contact__container grid'>
        {showForm && (
          <div className='contact__info'>
            <h3 className='contact__title'>Let's talk about everything!</h3>
            <p className='contact__details'>Don't like forms? Send me an email</p>
          </div>
        )}

        {showForm && (
          <form action='' ref={form} className='contact__form' onSubmit={sendEmail}>
            <div className='contact__form-group'>
              <div className='contact__form-div'>
                <input type='text' className='contact__form-input' placeholder='Insert your name' name='from_name' />
              </div>

              <div className='contact__form-div'>
                <input type='email' className='contact__form-input' placeholder='Insert your email' name='from_email' />
              </div>
            </div>

            <div className='contact__form-div'>
              <input type='text' className='contact__form-input' placeholder='Insert your subject' name='subject' />
            </div>

            <div className='contact__form-div contact__form-area contact__form-area'>
              <textarea name='message' id='' cols='30' rows='10' className='contact__form-input' placeholder='Write your message'></textarea>
            </div>

            <button className='btn' type='submit' value='Send'>
              Send Message
            </button>
          </form>
        )}

        {showSuccessMessage && <p className='success-message'>Message sent successfully!</p>}
      </div>
    </section>
  );
};

export default Contact;
